import { Link } from "react-router-dom";
import Message from "./Message";
import SongArtist from "./SongArtist";
import SongLyric from "./SongLyric";

const SongDetails = ({ search, lyric, bio }) => {
  if (!lyric || !bio) return null;
  return (
    <>
      {search === null ? lyric ? !bio ?  <Link to="/">Search again</Link>:<></>:<></>:<></>}

      {lyric.error || lyric.err || lyric.name === "AbortError" ? (
        <Message
          msg={`Error: The song "<em>${search.song}</em>" does not exist`}
        />
      ) : (
        <SongLyric title={search.song} lyric={lyric.lyrics} />
      )}
      {bio.artists ? (
        <SongArtist artist={bio.artists[0]} />
      ) : (
        <Message
          msg={`Error: the singer or the band "<em>${search.artist}</em>" does not exist`}
        />
      )}
    </>
  );
};

export default SongDetails;
