import { useHistory } from "react-router-dom";

import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const FavSongTable = ({ mySongs, handleDeleteSong }) => {
  let history = useHistory();

  const handleDirection = (id) => {
    console.log(id);
    history.push(`/${id}`);
  };
  // console.log(typeof mySongs.length);

  // dots: true,
  //   infinite: true,
  //   speed: 500,
  //   slidesToShow: 1,
  //   slidesToScroll: 1,
  //   cssEase: "linear",
  let settings = {
    className: "center",
    dots: true,
    // infinite: true,
    centerPadding: "20px",
    // slidesToShow: <SlideLength />,
    slidesToShow: mySongs.length - 1 > 7 ? 7 : mySongs.length - 1,
    swipeToSlide: false,
    afterChange: function (index) {
      console.log(
        `Slider Changed to: ${index + 1}, background: #222; color: #bada55`
      );
    },
  };
  return (
    <Slider {...settings}>
      {/* <Carousel
        showArrows={true}
        useKeyboardArrows
        ariaLabel="true"
        centerMode={true}
        infiniteLoop={true}
      > */}
      {/* <table>
        <thead>
          <tr>
            <th>Artists</th>
            <th>Song</th>
            <th>Accions</th>
          </tr>
        </thead>
        <tbody> */}
      {mySongs.length > 0 ? (
        mySongs.map((elem, index) => (
          <FavSongTableRow
            key={index}
            elem={elem}
            id={index}
            handleDeleteSong={handleDeleteSong}
            handleDirection={handleDirection}
          />
        ))
      ) : (
        <div className="container mt-5 carousel">
          <h3 colSpan="4">There are not favourite songs</h3>
        </div>
      )}
      {/* </tbody>
      </table> */}
      {/* </Carousel> */}
      {/* </div> */}
      {/* </div> */}
    </Slider>
  );
};

export default FavSongTable;
