        {mySongs.length > 0
          ? mySongs.map((elem, index) => (
              <FavSongTableRow
                key={index}
                elem={elem}
                id={index}
                handleDeleteSong={handleDeleteSong}
                handleDirection={handleDirection}
              />
            ))
          : {
              /* <tr>
              <td colSpan="4">There are not favourite songs</td>
            </tr> */
            }}
        {/* </tbody>
      </table> */}
      </Carousel>
    </div>
  );
};

export default FavSongTable;
