import React, { useState } from 'react';
import FavSongTableRow from "./FavSongTableRow";
// import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
// import { Carousel } from "react-responsive-carousel";
import { useHistory } from "react-router-dom";


import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const FavSongTable = ({ mySongs, handleDeleteSong }) => {

  let history = useHistory();
  const [link, setLink] = useState(false);

  const handleDirection = (id) => {
    console.log(id);
    history.push(`/${id}`);
  };

  let settings = {
    className: "center",
    dots: true,
    infinite: true,
    centerPadding: "20px",
    slidesToShow: mySongs.length - 1 > 7 ? 7 : mySongs.length - 1,
    swipeToSlide: false,
    afterChange: function (index) {
      console.log(
        `Slider Changed to: ${index + 1}, background: #222; color: #bada55`
      );
    },
  };


  return (
    <Slider {...settings}>
      {mySongs.length > 0 ? (
        mySongs.map((elem, index) => (
          <FavSongTableRow
            key={index}
            elem={elem}
            id={index}
            handleDeleteSong={handleDeleteSong}
            handleDirection={handleDirection}
          />
        ))
      ) : (
        <div className="container mt-5 carousel">
          <h3 colSpan="4">There are not favourite songs</h3>
        </div>
      )}
    </Slider>
  );
};

export default FavSongTable;
